declare module "nativescript-file-picker" {
    export interface Mode{
        MODE_FILE : number ,
        MODE_DIR : number ,
        MODE_FILE_AND_DIR : number,
        MODE_NEW_FILE :number
    }

    export var Modes:Mode;


    export class FilePicker {

        public allowMultiple:boolean;
        public allowCreateDir:boolean;
        public mode:number; 
        public singleClick:boolean;
        public startPath:string

        //methods
        public show():Promise<string[]>;

    }
}
